<?phpnamespace App\Models;use App\Models\Base as  Model;class Banner extends Model {	/**	 * The database table used by the model.	 *	 * @var string	 */	protected $table = 'banners';	protected $primaryKey = 'id';	public $timestamps = false;	protected $fillable = array('img','url','code','section','views','type','active');	public static $rules = array(			'img' => 'min:2|max:100',			'url' => 'max:250',			'code' => 'min:2',			'section' => 'required|in:top,side',			'views' => 'integer',			'type' => 'integer|in:1,0',			'active' => 'integer|in:1,0',	);	}