<?phpnamespace App\Models;use App\Models\Base as  Model;class Country extends Model{	/**	 * The database table used by the model.	 *	 * @var string	 */	protected $table = 'country';	protected $primaryKey = 'id';	public $timestamps = false;	protected $fillable = array('titleAr','titleEn','alpha_2');	public static $rules = array(		'titleAr' => 'required|min:2|max:100',		'titleEn' => 'required|min:2|max:100',		'alpha_2' => 'required|min:2|max:2',	);	public static function getCountriesMenu()	{		$countries = DB::table('country');		$data2 = $countries->lists('title'.LANG, 'id');		$data[] = trans('main.Choose');		// $countries = Country::all();		// $data = array();		// $data[0] = trans('main.Choose');		foreach ($data2 as $k => $v) {			$data[$k] = $v;		}		return $data;	}		public function users()	{		return $this->hasMany(User::class,'country_id');	}	public function cities()	{		return $this->hasMany(City::class);	}}