<?phpnamespace App\Models;use App\Models\Base as  Model;class Languages extends Model {	/**	 * The database table used by the model.	 *	 * @var string	 */	protected $table = 'languages';	protected $primaryKey = 'id';	public $timestamps = false;	protected $fillable = array('langName','langDirection','langShortName','langFlag');	public static $rules = array(		'langName' => 'required|min:2|max:20',		'langDirection' => 'required|size:3',		'langShortName' => 'required|min:2|max:5',		'langFlag' => 'min:2|max:3',	);    /**     * @return \Illuminate\Database\Eloquent\Relations\HasMany     */    public function vars()	{		return $this->hasMany(LanguagesVars::class);	}}