<?phpnamespace App\Models;use App\Models\Base as  Model;class LanguagesVars extends Model {	/**	 * The database table used by the model.	 *	 * @var string	 */	protected $table = 'languages_vars';	protected $primaryKey = 'id';	public $timestamps = false;	protected $fillable = array('key','value','defaults','lang_id');	public static $rules = array(		'key' => 'required|min:2|max:100',		'value' => 'min:2|max:100',		'defaults' => 'min:2|max:100',		'lang_id' => 'integer|required',	);    /**     * @return \Illuminate\Database\Eloquent\Relations\BelongsTo     */    public function language()	{		return $this->belongsTo(Languages::class,'lang_id','id');	}}