<?phpnamespace App\Models;use App\Models\Base as  Model;use App\Models\Clinic\Province;class Lesson extends Model {	/**	 * The database table used by the model.	 *	 * @var string	 */	protected $table = 'lessons';	protected $primaryKey = 'id';	// public $timestamps = false;	protected $fillable = array('title','province_id');	public static $rules = array(		'title' => 'required|min:2|max:200',	);	public static $public_rules = array(		'title' => 'required|min:2|max:200',		'content' => 'required|min:5',		'type_id' => 'required|in:lectures,lessons',		'name' => 'required|min:2',		'address' => 'required',		'province_id' => 'required|numeric',		'city_id' => 'required|numeric',		'phone' => 'required|numeric',		'dated' => 'max:255',		'photo' => 'max:200',		'date_start' => 'required|date',		'date_end' => 'required|date',		'facebook' => 'url',		'twitter' => 'url',		'youtube' => 'url',		'intagram' => 'url',		'live' => 'url',	);    /**     * @return \Illuminate\Database\Eloquent\Relations\BelongsTo     */    public function province()	{		return $this->belongsTo(Province::class);	}	////	public function city()////	{////		return $this->belongsTo(City::class);////	}}