<?phpnamespace App\Models;use App\Models\Base as  Model;class Role extends Model {	/**	 * The database table used by the model.	 *	 * @var string	 */	protected $table = 'roles';	protected $primaryKey = 'id';	public $timestamps = false;	protected $fillable = array('id','roleNameAr','roleNameEn','rolePermissions');	public static $rules = array(		'id' => 'required|integer|max:10|unique:roles',		'roleNameAr' => 'required|max:30',		'roleNameEn' => 'required|max:30',		'rolePermissions' => '',	);    /**     * @return \Illuminate\Database\Eloquent\Relations\HasMany     */    public function users()	{		return $this->hasMany(User::class);	}}