<?phpnamespace App\Models;use App\Models\Base as  Model;use Illuminate\Support\Facades\DB;class SiteMenu extends Model {	/**	 * The database table used by the model.	 *	 * @var string	 */	protected $table = 'site_menu';	protected $primaryKey = 'id';	public $timestamps = false;	protected $fillable = array('titleAr','titleEn','url','parent_id','menu_name','ordering','active','internal');	public static $rules = array(		'titleAr' => 'min:2|max:100',		'titleEn' => 'required|min:2|max:100',		'url' => '',		'ordering' => 'integer',		'parent_id' => 'integer',		'menu_name' => 'required|in:mainmenu,footer1menu,footer2menu,topmenu',		'active' => '',		'internal' => '',	);	public static function getMenuLinks()	{		$data[] = trans('main.Choose');		$parentLinks = DB::table('site_menu');		$parentLinks = $parentLinks->select(['title'.ucfirst(LANG_SHORT), 'id'])->get();		foreach ($parentLinks as $k ) {			$data[$k->id] = $k->{'title'.ucfirst(LANG_SHORT)};		}		return $data;	}	public function scopeOrdered($query)	{	    return $query->orderBy('ordering', 'asc')->get();	}    /**     * @return \Illuminate\Database\Eloquent\Relations\HasMany     */    public function links()	{		return $this->hasMany(SiteMenu::class,'parent_id','id');	}    /**     * @return \Illuminate\Database\Eloquent\Relations\BelongsTo     */    public function parentLink()	{		return $this->belongsTo(SiteMenu::class,'parent_id','id');	}}