<?phpnamespace App\Models;use App\Models\Base as  Model;class UserMeta extends Model {	/**	 * The database table used by the model.	 *	 * @var string	 */	protected $table = 'users_meta';	protected $primaryKey = 'id';	public $timestamps = false;	protected $fillable = array('user_id','metaKey','metaValue');		public static $rules = array(		'user_id' => 'required|integer',		'metaKey' => 'required|alpha_dash|min:2|max:200',		'metaValue' => '',	);	public function user()	{		return $this->belongsTo(User::class);	}}